,(SELECT  sum(CASE WHEN MONTH(nf.dt_emissao) IN ({{ month }}) THEN IFNULL((nf.vl_total_nota_fiscal),0) ELSE 0 END) FROM  vd_nota_fiscal AS nf WHERE  cliente.cod_cliente = nf.cod_cliente AND nf.dt_emissao >= {{ start_period }} AND nf.dt_emissao < {{ end_period }}  AND nf.situacao < 100 AND nf.cod_empresa IN {{ company_ids }} AND nf.nop IN ("6.102","6.404","BLACKFRIDAY","VENDA","VENDA_S_ESTOQUE","WORKSHOP")) AS {{month_year}}
,(SELECT  sum(CASE WHEN MONTH(nf.dt_emissao) IN ({{ month }}) THEN IFNULL(((nf.vl_total_tb_kami - nf.vl_total_nota_fiscal)),0) ELSE 0 END) FROM  vd_nota_fiscal AS nf WHERE  cliente.cod_cliente = nf.cod_cliente AND nf.dt_emissao >= {{ start_period }} AND nf.dt_emissao < {{ end_period }}  AND nf.situacao < 100 AND nf.cod_empresa IN {{ company_ids }} AND nf.nop IN ("6.102","6.404","BLACKFRIDAY","VENDA","VENDA_S_ESTOQUE","WORKSHOP")) AS {{month_year}}_desconto
,(SELECT  sum(CASE WHEN MONTH(nf.dt_emissao) IN ({{ month }}) THEN IFNULL((nf.vl_total_nota_fiscal),0) ELSE 0 END) FROM  vd_nota_fiscal AS nf WHERE  cliente.cod_cliente = nf.cod_cliente AND nf.dt_emissao >= {{ start_period }} AND nf.dt_emissao < {{ end_period }}  AND nf.situacao < 100 AND nf.cod_empresa IN {{ company_ids }} AND nf.nop IN ("BONIFICADO","BONI_COMPRA","PROMOCAO","PROMO_BLACK","CAMPANHA")) AS {{month_year}}_bonificado
,(SELECT  sum(CASE WHEN MONTH(nf.dt_emissao) IN ({{ month }}) THEN IFNULL((nf.vl_total_nota_fiscal),0) ELSE 0 END) FROM  vd_nota_fiscal AS nf WHERE  cliente.cod_cliente = nf.cod_cliente AND nf.dt_emissao >= {{ start_period }} AND nf.dt_emissao < {{ end_period }}  AND nf.situacao < 100 AND nf.cod_empresa IN {{ company_ids }} AND nf.nop IN ("ENXOVAL")) AS {{month_year}}_ENXOVAL